// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { h } from "@stencil/core";
export class Form {
    constructor() {
        this.value = {};
    }
    onButtonSubmit(e) {
        e.stopPropagation();
        this.value = this.getFormValue();
        this.onSubmit.emit({
            value: this.value
        });
    }
    onButtonReset(e) {
        e.stopPropagation();
        this.form.reset();
    }
    componentDidLoad() {
        this.value = this.getFormValue();
        Object.defineProperty(this.el, 'value', {
            get: () => this.value,
            configurable: true
        });
    }
    getFormValue() {
        const el = this.el;
        const elements = [];
        const tagElements = el.getElementsByTagName('input');
        for (let j = 0; j < tagElements.length; j++) {
            elements.push(tagElements[j]);
        }
        const formItem = {};
        const hash = {};
        elements.forEach(item => {
            if (item.className.indexOf('weui-switch') !== -1) {
                formItem[item.name] = item.checked;
                return;
            }
            if (item.type === 'radio') {
                if (item.checked) {
                    hash[item.name] = true;
                    formItem[item.name] = item.value;
                }
                else {
                    if (!hash[item.name]) {
                        formItem[item.name] = '';
                    }
                }
                return;
            }
            if (item.type === 'checkbox') {
                if (item.checked) {
                    if (hash[item.name]) {
                        formItem[item.name].push(item.value);
                    }
                    else {
                        hash[item.name] = true;
                        formItem[item.name] = [item.value];
                    }
                }
                else {
                    if (!hash[item.name]) {
                        formItem[item.name] = [];
                    }
                }
                return;
            }
            formItem[item.name] = item.value;
        });
        const textareaElements = el.getElementsByTagName('textarea');
        const textareaEleArr = [];
        for (let i = 0; i < textareaElements.length; i++) {
            textareaEleArr.push(textareaElements[i]);
        }
        textareaEleArr.forEach(v => {
            formItem[v.name] = v.value;
        });
        return formItem;
    }
    render() {
        return (h("form", { ref: dom => {
                if (dom) {
                    this.form = dom;
                }
            } },
            h("slot", null)));
    }
    static get is() { return "taro-form-core"; }
    static get events() { return [{
            "method": "onSubmit",
            "name": "submit",
            "bubbles": true,
            "cancelable": true,
            "composed": true,
            "docs": {
                "tags": [],
                "text": ""
            },
            "complexType": {
                "original": "any",
                "resolved": "any",
                "references": {}
            }
        }]; }
    static get elementRef() { return "el"; }
    static get listeners() { return [{
            "name": "tarobuttonsubmit",
            "method": "onButtonSubmit",
            "target": undefined,
            "capture": false,
            "passive": false
        }, {
            "name": "tarobuttonreset",
            "method": "onButtonReset",
            "target": undefined,
            "capture": false,
            "passive": false
        }]; }
}
